sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var gr = new GlideRecord("x_lbsa_gop_req_gop");
    gr.addQuery("sys_id", current.sys_id);
    gr.query();
    if (gr.next()) {

		gr.state=2;
        var fechaRecibir = new GlideDateTime(gr.u_slx_colaborador.u_slx_fecha_ingreso.toString() + " 13:00:00");
        var diasRecibir = Number(gr.variables.u_slx_dias_recibir_habiles);
        fechaRecibir.addDaysLocalTime(diasRecibir);

        var fechaTraslado = new GlideDateTime(gr.u_slx_colaborador.u_slx_fecha_ingreso.toString() + " 13:00:00");
        var diasTraslado = Number(gr.variables.u_slx_dias_traslado);

        fechaTraslado.addDaysLocalTime(diasTraslado);

		var fechaEncuesta = new GlideDateTime(gr.u_slx_colaborador.u_slx_fecha_ingreso.toString() + " 13:00:00");
        var diasEncuesta = Number(gr.variables.u_slx_dias_encuesta);

        fechaEncuesta.addDaysLocalTime(diasEncuesta);




        var sched = new GlideSchedule(gs.getProperty('x_lbsa_gop.calendario.gop'));
        var date = new GlideDateTime(fechaRecibir);



        while (!sched.isInSchedule(fechaRecibir)) {
            fechaRecibir.addDaysLocalTime(-1);

        }


        while (!sched.isInSchedule(fechaTraslado)) {
            fechaTraslado.addDaysLocalTime(-1);

        }

		while (!sched.isInSchedule(fechaEncuesta)) {
            fechaEncuesta.addDaysLocalTime(1);

        }
		gr.variables.u_slx_fecha_encuesta = fechaEncuesta;
        gr.variables.u_slx_fecha_traslado = fechaTraslado;
        gr.variables.u_slx_fecha_recibir_colaborador = fechaRecibir;
        gr.u_slx_flujo = true;
        gr.update();

    }

})(current, previous);
