sys_script.script:
(function executeRule(current, previous /*null when async*/) {
// 1 = Abierto , 200 = Asignado , 2 = En curso , -5 = Pendiente 
// 3 = Cerrado Completado , 4 = Cerrado incompleto , 8 = Cancelado 
// solo contempla cammbios en: 1 = Abierto , 200 = Asignado , 2 = En curso y si cambia grupo o asignado

	var soloasignado = true; //variable para determinar que solo el asignado cambia de estado

	//if (gs.getUser().getRecord().getValue('web_service_access_only') ==true) soloasignado = false; // quita condición de asignado a usuario integración
	if (gs.getUser().hasRole('admin')||gs.getUser().hasRole('x_lbsa_gop.seleccion')) soloasignado = false; 


	if (current.active==true)
		{
		if( current.state!=200 && current.assignment_group!='' && ((current.assigned_to=='') || (current.assigned_to!=gs.getUserID() && (soloasignado))))  
			{
			current.state = 200;
		}else{
			if((current.state == 200) || ( current.state == 1) )
			{
				if(current.assignment_group!=''&& current.assigned_to==gs.getUserID())
					{
					current.state = 2;
					if (current.work_start=='') {current.work_start = new GlideDateTime();
					}
				}else{
					if(current.assignment_group =='' && current.assigned_to=='')
						{
						current.state = 1;
						}
				}
			}else{

				if ((current.state == 2) && (current.work_start=='')) 
						{current.work_start = new GlideDateTime();}
			}
		}
	}
})(current, previous);
