sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading ) {
      return;
   }

	var isAdmin = g_user.hasRoleFromList ("x_lbsa_gop.seleccion, admin");
	var isActive = g_form.getValue("active");

	if (isActive && oldValue != newValue && (newValue === '' || newValue == null ) && !isAdmin)
		{
			g_form.addOption("state", 200, 'Asignado');
			g_form.setValue("state", 200 );
			g_form.setReadOnly("state",true);
		}
//isActive && 
	if (isActive && newValue == g_user.userID)
		{
			g_form.addOption("state", 2, 'En Progreso');
			g_form.setValue("state", 2);
			g_form.removeOption("state", 200);
			g_form.setReadOnly("state",false);
		}

}
