sys_script_client.script:
function onLoad() {  
// 1 = abierto, 200 = asignado, 2 = en curso, -5 = pendiente,
// 3 = cerrado completado, 4 = cerrado incompleto, 7 = cancelado

	var taskSta = g_form.getValue('state');
	var noAssig = g_form.getValue('assigned_to') == '';

	if ((taskSta != 1)){ g_form.removeOption('state', '1'); }     
	if ((taskSta != 200)){ g_form.removeOption('state', '200'); } 
	if ((taskSta != 2 && taskSta != -5)) {                // si no es activo (en curso o pendiente)...
		g_form.removeOption('state', '-5');                       // saca pendiente
		if (noAssig) {g_form.removeOption('state', '2');}         // si no estÃ¡ asignado, saca en curso
		if (taskSta != 3 ) {g_form.removeOption('state', '3');}   // no completado, saca completado
		if (taskSta != 4 ) {g_form.removeOption('state', '4');} 
		if (taskSta != 7 ) {g_form.removeOption('state', '7');} 
		}
}
