sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here

    var tabla1 = '<table style="border-collapse: collapse; width: 100%;" border="1">' +
        '<tbody>' + '<tr>' + '<td style="width: 33.3333%;"><strong><span style="font-size: 12.0pt; font-family: Verdana, sans-serif; color: black;">Tipo de Curso</strong></td>' + '<td style="width: 33.3333%;"><strong><span style="font-size: 12.0pt; font-family: Arial, sans-serif; color: black;">Nombre del Curso</strong></td>';

	var tabla2='';
	 var finTabla = '</tr>' + '</tbody>' + '</table>';
    var gr = new GlideRecord("asmt_metric_result");
    gr.addQuery("source_id", current.sys_id);
    gr.addQuery("metric.category", "55ee8d481b30f1107c132170f54bcb3b");
    gr.query();
    gr.orderBy("actual_value");
    while (gr.next()) {
        var tipo = gr.metric.u_slx_tipo_curso.getDisplayValue();
    //    template.print("<b>Tipo:</b> " + tipo + " <b>Curso</b>: " + gr.string_value + "<br>");

		tabla2 += '<tr><td style=\"padding: 5px;font-family: Arial, sans-serif; border: 1px solid; border-color: grey;\">' +
                    tipo + "</td><td style=\"padding: 5px;font-family: Arial, sans-serif; border: 1px solid; border-color: grey;\">" +
                    gr.string_value + "</td>";

    }
template.print(tabla1 + tabla2 + finTabla);



})(current, template, email, email_action, event);
