sys_script_include.script:
var slx_validacion_datos = Class.create();
slx_validacion_datos.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    colaboradores: function(elemento) {

        var contrato = "";

        if (elemento == gs.getProperty('x_lbsa_gop.plazo.fijo')) {
            contrato = '1';

        } else if (elemento == gs.getProperty('x_lbsa_gop.indefinido')) {
            contrato = '2';
        } else if (elemento == gs.getProperty('x_lbsa_gop.practica')) {
            contrato = '3';
        }



        var lista = "";
        var gr = new GlideRecord("x_lbsa_gop_colaborador");
		gr.addQuery('u_slx_tipo_contrato',contrato);
        gr.query();
        while (gr.next()) {


            var gop = new GlideRecord("x_lbsa_gop_req_gop");
            gop.addQuery('u_slx_colaborador', gr.sys_id);
            gop.query();
            if (!gop.next()) {
                lista = gr.sys_id + ",";
            }


        }
        return 'sys_idIN'+lista;

    },
    type: 'slx_validacion_datos'
});
