sys_script_include.script:
var slx_actualizar_gop = Class.create();
slx_actualizar_gop.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
cancelarGop: function() {
        var gop = this.getParameter('sysparm_gop');

        var gr = new GlideRecord("x_lbsa_gop_req_gop");
        gr.addQuery("sys_id", gop);
		gr.addQuery("active", true);
        gr.query();
        if (gr.next()) {
			gr.state=7;
			gr.update();
			return '200';
        }else
			{
				return 404;
			}

    },
    type: 'slx_actualizar_gop'
});
