sys_ui_action.script:
function cancelar() {


    var short_text = "Recuerde argumentar la cancelación con comentarios internos.";
    //Initialize and open the Dialog Window

    var dialog = new GlideDialogWindow("x_lbsa_gop_task_comments_dialog");
    dialog.setTitle("Cancelación de Requerimiento"); //Set the dialog title
    dialog.setPreference("short_text", short_text);
    dialog.setSize(500, 500);
    dialog.render(); //Open the dialog

    g_form.setMandatory('work_notes', true);
    var notes = g_form.getValue('work_notes');
    if (notes != '') {
        var gop = g_form.getUniqueValue();

        var sp = new GlideAjax("x_lbsa_gop.slx_actualizar_gop");
        sp.addParam("sysparm_name", "cancelarGop");
        sp.addParam("sysparm_gop", gop);
        sp.getXML(callback);
    }

}

function callback(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    if (answer == 200 || answer == '200') {
        gsftSubmit(null, g_form.getFormElement(), 'slx_cancelar');
    } else {
        //g_form.addErrorMessage('Ocurrio un error al intentar aprobar, favor contactarse con el administrador');
    }


}


// //Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if (typeof window == 'undefined')
    updateTask();

function updateTask() {
    //current.state = 3;
    current.update();
    action.setRedirectURL(current);
}
