sys_ui_policy.script_true:
function onCondition() {

	var sp = new GlideAjax("global.slx_valida_campos");
    sp.addParam("sysparm_name", "valida_servicio");
    sp.addParam("sysparm_service_off", g_form.getValue("service_offering"));
    sp.getXML(callback);

}

function callback(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");

    var fields = answer.split(",");
    for (var i = 0; i < fields.length; i++) {
        if (String(fields[i]) != "") {
			g_form.setDisplay(String(fields[i]), true);
            g_form.setVisible(String(fields[i]), true);
        }
    }

}
