var completo = 0;
var incompleto = 0;
var omitido = 0;
var state="";

var gr = new GlideRecord("x_lbsa_gop_tarea_gop");
gr.addQuery("parent", current.sys_id);
gr.query();
var q=gr.getRowCount();
while (gr.next()) {
    if (gr.state == 3) {
        completo++;
    } else if (gr.state == 4) {
        incompleto++;
    } else if (gr.state == 7) {
        omitido++;
    }

}

if(completo==q)
{
    state="completo";
}
if(omitido==q)
{
    state="omitido";
}
if(incompleto>=1)
{
    state="incompleto";
}
if(omitido>=1 && completo>=1)
{
    state="completo";
}

