//Creación RITM TI Ingreso nuevo usuario interno

var usuarioAfectado = current.opened_by;
var colaborador = current.u_slx_colaborador;
var ubicacion = '';
var tipoIngreso = current.u_slx_tipo_contrato;
var ingreso = current.u_slx_colaborador.u_slx_fecha_ingreso;
var desc = "Ingreso de nuevo colaborador en proceso de OB " + current.u_slx_colaborador.getDisplayValue() + " Responsable del proceso " + current.u_slx_responsable_proceso.getDisplayValue();
 var elementoSysid = gs.getProperty('x_lbsa_gop.alta.usuario.interno.ti');

var colab=new GlideRecord('x_lbsa_gop_colaborador');
colab.addQuery('sys_id',colaborador);
colab.query();
var colabId="";
if(colab.next())
	{
		colabId=colab.sys_id;
	}

var cart = new sn_sc.CartJS();
var item =
{
'sysparm_id': elementoSysid,
'sysparm_quantity': '1',
'variables':{
'usuario_afectado': gs.getUserID(),
'ubicacion_actual': ubicacion,
'nuevo_colaborador': String(colabId),
'tipo_de_ingreso': 'plazo_indefinido',
'fecha_de_ingreso': ingreso, 
'descripcion_requerimiento': desc, 	
}};
//var cartDetails = cart.addToCart(item);
cart.setRequestedFor(current.opened_by);
var cartDetails = cart.orderNow(item);
var req=String(cartDetails.request_id);
var gr = new GlideRecord("sc_req_item");
gr.addQuery("request", req);
gr.query();
if (gr.next()) {
	
	task.u_slx_ritm=gr.sys_id;
   
}

